INSERT INTO form VALUES ('6c544723-241c-4896-a38c-adbc0a364293', 'submission_request', 1, 'Submission Request Form', 'This form is used to get high level information about a dataset, typically this will be submitted by the data provider or an appropriate agent.', 'To express interest in archiving a data product, please fill out the following form.');
