INSERT INTO section VALUES ('065fe2a6-8f83-403b-a3f0-49e8cff9358a', '6c544723-241c-4896-a38c-adbc0a364293', 'Data Product Information', 2, '[]', '[]');
INSERT INTO section VALUES ('d54dccae-2818-4918-9b0e-3bb7e629c55b', '6c544723-241c-4896-a38c-adbc0a364293', 'Data Product Coverage', 3, '[]', '[]');
INSERT INTO section VALUES ('32b3f1c1-a419-43a3-89ea-e47e1f015da3', '6c544723-241c-4896-a38c-adbc0a364293', 'Data Product Technical Details', 4, '[]', '[]');
INSERT INTO question VALUES ('80ac5f52-9ed9-4139-b5f9-7b4cebb6a8e2', 'data_producer_info', 1, 'Primary Data Producer', 'Who is the primary person responsible for the collection or creation of this data product?', 'Often this is the Principle Investigator, Project Scientist, or Project Manager');
INSERT INTO question VALUES ('f3e2eab9-6375-4e53-9cc2-3d16f318d332', 'submission_request_poc', 1, 'Submission Request Point of Contact', 'Who should the DAAC contact with questions regarding this data submission request?', 'This person should have in-depth knowledge of this data product, allowing them to provide additional information as needed.');
INSERT INTO question VALUES ('8a364184-42ac-48fe-b831-acb2eb08c728', 'funding_organization', 1, 'Funding Source: Organization', 'What organization(s) funded the creation of this data product?', 'This the organization that funded the creation of the data product.');
INSERT INTO question VALUES ('4ecc885f-daf8-4bc6-a8cd-d30c2a54d085', 'funding_program', 1, 'Funding source: Program/Program Element', 'Under what program or program element within the funding organization was this data product created?', 'For example, NASA programs such as MEaSUREs, Terrestrial Hydrology, Earth Venture, ACCESS, or AIST.');
INSERT INTO question VALUES ('f74c6c20-7483-40f9-a63e-58cc20ae8c8f', 'data_product_name', 1, 'Data Product Name', 'How do you refer to this data product?', 'This is how the DAAC will refer to this data product during the data submission request process.');
INSERT INTO question VALUES ('9767336b-a9a9-41d2-8d2d-4fc2858c9b6f', 'data_product_description', 1, 'Data Product Description', 'Please provide a brief description of this data product following the guidance in the help text.', 'The description should mimic a journal abstract and should provide a reader with the information needed to quickly understand the relevance and usefulness of the data.');
INSERT INTO question VALUES ('ab57f5e8-9ec5-46c9-978e-d06125346d36', 'publication', 1, 'Publication(s)', 'Please provide the citation or Digital Object Identifier (DOI) for any publications describing this data product.', 'For example, a overview paper describing the research, methods, or initial results related to this data product.');
INSERT INTO question VALUES ('7fd7bccf-5065-4033-9956-9e80bc99c205', 'science_value', 1, 'Science Value of Data Product', 'Please describe the science value of this data product.', 'For example, how will this data product benefit the science community? What are the strengths and the limitations of this data product? What other data products does it compliment?');
INSERT INTO question VALUES ('bd00dbb7-1d3c-46fa-82a4-734236f4e06c', 'data_submission_reason', 1, 'Reason for Data Submission Request', 'Please briefly explain your reasons for requesting approval to publish this data product at the DAAC.', 'For example, are you attempting to publish a paper which requires data to be archived in a trusted repository? Have you been instructed by a NASA program manager to submit your data to the DAAC? Do you want this data product archived with related data?');
INSERT INTO question VALUES ('f40956c3-9af8-400e-8dd8-c5e2965dcb8a', 'data_submission_approval_dependencies', 1, 'Dependencies for Data Submission Approval', 'Do you have any dependencies related to this data product being approved to be published at the DAAC? If ''Yes'', please provide a brief explanation.', 'For example, are you hoping to have this data product approved to be published at the DAAC prior publishing a paper or presenting at a conference? Does your project have a defined timeline in which this data product needs to be published?');
INSERT INTO question VALUES ('2dd6c8b1-22a8-4866-91c3-da9b4ce849dc', 'data_product_restrictions', 1, 'Data Product Restrictions', 'Can this data product be publicly released in compliance with NASA''s Open Data Policy? If No, please provide a brief explanation.', 'For a description of the policy, please refer to the NASA Earthdata Data and Information Policy web page (https://earthdata.nasa.gov/collaborate/open-data-services-and-software/data-information-policy).');
INSERT INTO question VALUES ('ad568b2f-89fe-4afd-a0bf-9e5832b71ce9', 'data_product_documentation', 1, 'Additional Documentation', 'Please upload additional documents that describe this data product or provide URLs to additional documents.', 'For example, these documents may include descriptions of the variables, filename conventions, processing steps, and/or data quality.');
INSERT INTO question VALUES ('c9c73e88-837a-42d2-aa1a-50874a333607', 'data_product_doi', 1, 'Data Product DOI', 'If a Digital Object Identifier (DOI) already exists for this data product, please provide it.', 'This would be the DOI for the actual data product and not for a publication related to this data product.');
INSERT INTO question VALUES ('4f2dd369-d865-47ba-8504-8694493f128f', 'data_product_temporal_coverage', 1, 'Temporal Coverage', 'What period of time is covered by the entire data product?', 'The temporal coverage should encompass the beginning date of the first data file and the ending date of the last data file, even if there are time gaps.');
INSERT INTO question VALUES ('f4d8ab9d-0959-41b8-8449-430986ddfe81', 'data_product_status', 1, 'Data Product Status', 'After this data product has been published at the DAAC, will you continue to collect or create new data to extend the time series?', '');
INSERT INTO question VALUES ('f4d8ab9d-0959-41b8-8449-430986ddfe84', 'data_delivery_frequency', 1, 'Data Delivery Frequency', 'What is the anticipated frequency of additional data deliveries to the DAAC?', 'undefined');
INSERT INTO question VALUES ('ebeab400-b3d7-4943-8d09-d1236afc5de0', 'data_product_spatial_coverage', 1, 'Data Product Spatial Coverage', 'What is the general geographic region covered by this data product?', 'For example, Global, Northern Hemisphere, Alaska, Korean Peninsula, East Tropical Pacific, or Gulf Stream.');
INSERT INTO question VALUES ('50e8d566-b9ab-4bd9-9adc-92a3c8fb5d27', 'data_format', 1, 'Data Format', 'In what format(s) are the files in this data product? If selecting ''Other'', please upload code to read the data or provide a URL to code to read the data.', 'For a list of NASA-approved data formats, please refer to the NASA Earthdata Standards and Practices web page (https://earthdata.nasa.gov/esdis/eso/standards-and-references#data-formats).');
INSERT INTO question VALUES ('40672516-2220-4edc-8c1b-fd9f7e0b978e', 'data_product_volume', 1, 'Data Product Volume', 'What is the estimated or actual total volume of this data product?', 'The DAAC uses the total volume of the final data product to plan data storage requirements. If the final data product is not complete, please provide your best estimate for the total data volume.');
INSERT INTO question VALUES ('53a0faa7-f7d4-4ce9-a9dc-a13cef44e1f3', 'sample_data_file', 1, 'Sample Data File(s)', 'Please upload a sample data file(s) or provide a URL to sample file(s).', 'Providing data files that are representative of the range of data within this data product will help the DAAC understand the data format, structure, and content.');
